def get_lists_of_dtypes(df):    """Helper function to create list of features by type."""    strings = list()    integers = list()    floats = list()    for x in df.columns:        if x != "Churn":            if str(df[x].dtype)[:3] in 'obj':                strings.append(x)            elif str(df[x].dtype)[:3] in 'int':                integers.append(x)            elif str(df[x].dtype)[:3] in 'flo':                floats.append(x)            else:                continue        else:                continue    return strings, integers, floatsdef yn_recode(s):    """Helper function to recode yes to 1, no to 0"""    if s.lower() == "yes":        return 1    elif s.lower() == "no":        return 0    else:        return np.nan