# test integration helper_functions.pyimport numpy as npimport pandas as pdimport pytestfrom tests.helper_functions import get_lists_of_dtypes, yn_recodedef test_integration_yn_recode_list_dtypes():    """Test the integration of functions in helper_functions.py"""        test_df = pd.DataFrame(["yes", "no", "NO", "yEs"], columns=["ynstrs"])        test_df['ynstrs_recode'] = test_df['ynstrs'].apply(yn_recode)        my_str, my_int, my_flo = get_lists_of_dtypes(test_df)        assert my_int == ['ynstrs_recode']